rule "NGINX_LOG_PARSE"
when contains(to_string($message.kubernetes_labels_app), "nginx-public-ingress", true)
then
set_fields(grok("%{NGINX_LOG_PARSE}", to_string($message.message), true));
set_field("n_rstime", to_double($message.n_rstime));
set_field("n_rqtime", to_double($message.n_rqtime));
set_field("n_rssize", to_long($message.n_rssize));
set_field("n_rscode", to_long($message.n_rscode));
end

rule "KONG_LOG_PARSE"
when contains(to_string($message.kubernetes_labels_app), "apimanager", true)
then
set_fields(grok("%{KONG_LOG_PARSE}", to_string($message.message), true));
set_field("k_rscode", to_long($message.k_rscode));
set_field("k_rqsize", to_long($message.k_rqsize));
end

rule "GEO_IP_LOOKUP"
when has_field("n_cip")
then
let geo = lookup("geoip", to_string($message.n_cip));
set_field("n_cip_geolocation", geo["coordinates"]);
set_field("n_cip_geo_country_code", geo["country"].iso_code);
set_field("n_cip_geo_country_name", geo["country"].names.en);
set_field("n_cip_geo_city_name", geo["city"].names.en);
end

rule "DRUID_ANAMOLY_PIPELINE"
when has_field("message")
then
let json = parse_json(to_string($message.message));
let new_fields = select_jsonpath(json, { dimensions_id: "$['dimensions'][0]['id']", dimensions_value: "$['dimensions'][0]['value']"
                                         metric_id: "$['metric']['id']", metric_value: "$['metric']['value']"});
set_fields(new_fields);
end
